import React, { useState, useEffect } from 'react';
import { reportAPI, assignmentAPI } from '../services/api';

const ReportForm = ({ user }) => {
  const [formData, setFormData] = useState({
    faculty: user.faculty,
    class_id: '',
    week_number: '',
    date_of_lecture: '',
    course_id: '',
    students_present: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });
  const [classes, setClasses] = useState([]);
  const [courses, setCourses] = useState([]);
  const [myAssignments, setMyAssignments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    loadFormData();
  }, [user]);

  const loadFormData = async () => {
    try {
      const [classesResponse, coursesResponse, assignmentsResponse] = await Promise.all([
        assignmentAPI.getClasses(),
        assignmentAPI.getCourses(),
        user.role !== 'student' ? assignmentAPI.getMyAssignments() : Promise.resolve({ data: [] })
      ]);

      setClasses(classesResponse.data);
      setCourses(coursesResponse.data);
      setMyAssignments(assignmentsResponse.data);

      // Set default class for students
      if (user.role === 'student' && user.class_id) {
        setFormData(prev => ({ ...prev, class_id: user.class_id }));
      }
    } catch (error) {
      console.error('Error loading form data:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await reportAPI.create(formData);
      setMessage('Report created successfully! Waiting for student signature.');
      setFormData({
        faculty: user.faculty,
        class_id: '',
        week_number: '',
        date_of_lecture: '',
        course_id: '',
        students_present: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: ''
      });
    } catch (error) {
      setMessage(error.response?.data?.message || 'Error creating report');
    } finally {
      setLoading(false);
    }
  };

  // Filter courses based on assignments for lecturers
  const availableCourses = user.role === 'student' 
    ? courses 
    : courses.filter(course => 
        myAssignments.some(assignment => assignment.course_id === course.id)
      );

  return (
    <div className="form-container">
      <div className="form-card">
        <h2>{user.role === 'student' ? 'View Class Reports' : 'Create Lecture Report'}</h2>
        
        {message && (
          <div className={`message ${message.includes('success') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}

        {user.role !== 'student' ? (
          <form onSubmit={handleSubmit}>
            <div className="form-grid">
              <div className="form-group">
                <label>Faculty</label>
                <select name="faculty" value={formData.faculty} onChange={handleChange} required>
                  <option value="FICT">FICT</option>
                  <option value="FBMG">FBMG</option>
                  <option value="FENG">FENG</option>
                </select>
              </div>

              <div className="form-group">
                <label>Class</label>
                <select name="class_id" value={formData.class_id} onChange={handleChange} required>
                  <option value="">Select Class</option>
                  {classes.map(cls => (
                    <option key={cls.id} value={cls.id}>{cls.class_name}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Week Number</label>
                <input
                  type="number"
                  name="week_number"
                  value={formData.week_number}
                  onChange={handleChange}
                  min="1"
                  max="52"
                  required
                />
              </div>

              <div className="form-group">
                <label>Date of Lecture</label>
                <input
                  type="date"
                  name="date_of_lecture"
                  value={formData.date_of_lecture}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Course</label>
                <select name="course_id" value={formData.course_id} onChange={handleChange} required>
                  <option value="">Select Course</option>
                  {availableCourses.map(course => (
                    <option key={course.id} value={course.id}>
                      {course.course_name} ({course.course_code})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Students Present</label>
                <input
                  type="number"
                  name="students_present"
                  value={formData.students_present}
                  onChange={handleChange}
                  min="1"
                  required
                />
              </div>

              <div className="form-group">
                <label>Venue</label>
                <input
                  type="text"
                  name="venue"
                  value={formData.venue}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Scheduled Time</label>
                <input
                  type="time"
                  name="scheduled_time"
                  value={formData.scheduled_time}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group full-width">
                <label>Topic Taught</label>
                <textarea
                  name="topic_taught"
                  value={formData.topic_taught}
                  onChange={handleChange}
                  rows="3"
                  required
                />
              </div>

              <div className="form-group full-width">
                <label>Learning Outcomes</label>
                <textarea
                  name="learning_outcomes"
                  value={formData.learning_outcomes}
                  onChange={handleChange}
                  rows="3"
                  required
                />
              </div>

              <div className="form-group full-width">
                <label>Recommendations</label>
                <textarea
                  name="recommendations"
                  value={formData.recommendations}
                  onChange={handleChange}
                  rows="3"
                />
              </div>

              <div className="form-actions full-width">
                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? 'Creating Report...' : 'Create Report'}
                </button>
              </div>
            </div>
          </form>
        ) : (
          <ClassReportsView user={user} />
        )}
      </div>
    </div>
  );
};

// Component for students to view and sign reports
const ClassReportsView = ({ user }) => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadClassReports();
  }, [user]);

  const loadClassReports = async () => {
    try {
      const response = await reportAPI.getClassReports(user.class_id);
      setReports(response.data);
    } catch (error) {
      console.error('Error loading reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSignReport = async (reportId) => {
    if (window.confirm('Are you sure you want to sign this report? This confirms the topics were taught and attendance is correct.')) {
      try {
        const signature = `Signed by ${user.name} (${user.email}) on ${new Date().toLocaleDateString()}`;
        await reportAPI.signReport(reportId, signature);
        alert('Report signed successfully! Sent to PRL for approval.');
        loadClassReports();
      } catch (error) {
        alert('Error signing report: ' + (error.response?.data?.message || 'Unknown error'));
      }
    }
  };

  if (loading) return <div>Loading reports...</div>;

  return (
    <div className="data-table">
      <table style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Course</th>
            <th>Week</th>
            <th>Date</th>
            <th>Students Present</th>
            <th>Topic</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map(report => (
            <tr key={report.id}>
              <td>{report.course_name}</td>
              <td>Week {report.week_number}</td>
              <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
              <td>{report.students_present}</td>
              <td>{report.topic_taught.substring(0, 50)}...</td>
              <td>
                <span className={`status-${report.status}`}>
                  {report.status.replace('_', ' ').toUpperCase()}
                </span>
              </td>
              <td>
                {report.status === 'pending_student' && (
                  <button 
                    onClick={() => handleSignReport(report.id)}
                    className="btn btn-primary"
                    style={{ padding: '0.25rem 0.5rem', fontSize: '0.8rem' }}
                  >
                    Sign Report
                  </button>
                )}
                {report.status !== 'pending_student' && (
                  <span>No action needed</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {reports.length === 0 && <div style={{ padding: '2rem', textAlign: 'center' }}>No reports found for your class</div>}
    </div>
  );
};

export default ReportForm;
