// Component for viewing reports list
const ReportsListView = ({ reports, user, onSignReport, onApproveReport }) => {
  const getStatusBadge = (status) => {
    const statusConfig = {
      draft: { label: 'Draft', color: '#95a5a6' },
      pending_student: { label: 'Pending Student Sign', color: '#f39c12' },
      pending_prl: { label: 'Pending PRL Approval', color: '#e67e22' },
      approved: { label: 'Approved', color: '#27ae60' }
    };
    
    const config = statusConfig[status] || { label: status, color: '#95a5a6' };
    return (
      <span style={{
        background: config.color,
        color: 'white',
        padding: '0.25rem 0.5rem',
        borderRadius: '12px',
        fontSize: '0.8rem',
        fontWeight: 'bold'
      }}>
        {config.label}
      </span>
    );
  };

  return (
    <div className="data-table">
      <h3 style={{ marginBottom: '1rem', color: '#2c3e50' }}>
        {user.role === 'student' ? 'Class Reports' : 'My Reports'}
      </h3>
      <table style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Course</th>
            <th>Class</th>
            <th>Week</th>
            <th>Date</th>
            <th>Students</th>
            <th>Status</th>
            <th>Next Step</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map(report => (
            <tr key={report.id}>
              <td>{report.course_name}</td>
              <td>{report.class_name}</td>
              <td>Week {report.week_number}</td>
              <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
              <td>{report.students_present}</td>
              <td>{getStatusBadge(report.status)}</td>
              <td>
                {report.status === 'draft' && 'Submit for student signing'}
                {report.status === 'pending_student' && 'Waiting for student signature'}
                {report.status === 'pending_prl' && 'Waiting for PRL approval'}
                {report.status === 'approved' && 'Completed'}
              </td>
              <td>
                {user.role === 'student' && report.status === 'pending_student' && (
                  <button 
                    onClick={() => onSignReport(report.id)}
                    className="btn btn-primary"
                    style={{ padding: '0.25rem 0.5rem', fontSize: '0.8rem' }}
                  >
                    Sign
                  </button>
                )}
                {(user.role === 'prl' || user.role === 'pl') && report.status === 'pending_prl' && (
                  <button 
                    onClick={() => onApproveReport(report.id)}
                    className="btn btn-primary"
                    style={{ padding: '0.25rem 0.5rem', fontSize: '0.8rem' }}
                  >
                    Approve
                  </button>
                )}
                {((user.role === 'prl' || user.role === 'pl') && report.status === 'approved') && (
                  <span style={{ color: '#27ae60', fontSize: '0.8rem' }}> Approved</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {reports.length === 0 && (
        <div style={{ padding: '2rem', textAlign: 'center', color: '#7f8c8d' }}>
          No reports found
        </div>
      )}
    </div>
  );
};

// Component for PRL/PL to view pending approval reports
const PendingApprovalView = ({ user, onApproveReport }) => {
  const [pendingReports, setPendingReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadPendingReports();
  }, [user]);

  const loadPendingReports = async () => {
    try {
      // This would need a new API endpoint to get reports pending PRL approval
      const response = await reportAPI.getMyReports();
      const pending = response.data.filter(report => report.status === 'pending_prl');
      setPendingReports(pending);
    } catch (error) {
      console.error('Error loading pending reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending_prl: { label: 'Pending Your Approval', color: '#e67e22' }
    };
    
    const config = statusConfig[status] || { label: status, color: '#95a5a6' };
    return (
      <span style={{
        background: config.color,
        color: 'white',
        padding: '0.25rem 0.5rem',
        borderRadius: '12px',
        fontSize: '0.8rem',
        fontWeight: 'bold'
      }}>
        {config.label}
      </span>
    );
  };

  if (loading) {
    return <div>Loading pending reports...</div>;
  }

  return (
    <div className="data-table">
      <h3 style={{ marginBottom: '1rem', color: '#2c3e50' }}>Reports Pending Your Approval</h3>
      <table style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Course</th>
            <th>Class</th>
            <th>Lecturer</th>
            <th>Week</th>
            <th>Date</th>
            <th>Students</th>
            <th>Topic</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pendingReports.map(report => (
            <tr key={report.id}>
              <td>{report.course_name}</td>
              <td>{report.class_name}</td>
              <td>{report.lecturer_name}</td>
              <td>Week {report.week_number}</td>
              <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
              <td>{report.students_present}</td>
              <td>{report.topic_taught.substring(0, 50)}...</td>
              <td>{getStatusBadge(report.status)}</td>
              <td>
                <button 
                  onClick={() => onApproveReport(report.id)}
                  className="btn btn-primary"
                  style={{ padding: '0.25rem 0.5rem', fontSize: '0.8rem' }}
                >
                  Approve
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {pendingReports.length === 0 && (
        <div style={{ padding: '2rem', textAlign: 'center', color: '#7f8c8d' }}>
          No reports pending your approval
        </div>
      )}
    </div>
  );
};

