import React, { useState, useEffect } from 'react';
import { reportAPI, complaintAPI, assignmentAPI } from '../services/api';

const Dashboard = ({ user, onLogout }) => {
  const [dashboardData, setDashboardData] = useState({
    reports: [],
    complaints: [],
    assignments: [],
    stats: {
      totalReports: 0,
      pendingReports: 0,
      totalComplaints: 0,
      pendingComplaints: 0,
      totalAssignments: 0
    }
  });
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    loadDashboardData();
  }, [user]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      let reports = [];
      let complaints = [];
      let assignments = [];

      // Load data based on user role
      switch (user.role) {
        case 'pl':
        case 'prl':
        case 'fmg':
          // For management roles, load pending approval reports and all complaints
          const [reportsResponse, complaintsResponse, assignmentsResponse] = await Promise.all([
            reportAPI.getPendingApprovalReports().catch(() => ({ data: [] })),
            complaintAPI.getComplaintsForMe().catch(() => ({ data: [] })),
            assignmentAPI.getAllAssignments ? assignmentAPI.getAllAssignments().catch(() => ({ data: [] })) : Promise.resolve({ data: [] })
          ]);
          
          reports = reportsResponse.data;
          complaints = complaintsResponse.data;
          assignments = assignmentsResponse.data || [];
          break;

        case 'lecturer':
          // For lecturers, load their reports and complaints
          const [lecturerReports, lecturerComplaints, lecturerAssignments] = await Promise.all([
            reportAPI.getMyReports().catch(() => ({ data: [] })),
            complaintAPI.getMyComplaints().catch(() => ({ data: [] })),
            assignmentAPI.getMyAssignments().catch(() => ({ data: [] }))
          ]);
          
          reports = lecturerReports.data;
          complaints = lecturerComplaints.data;
          assignments = lecturerAssignments.data;
          break;

        case 'student':
          // For students, load class reports and their complaints
          const [studentReports, studentComplaints] = await Promise.all([
            reportAPI.getClassReports(user.class_id).catch(() => ({ data: [] })),
            complaintAPI.getMyComplaints().catch(() => ({ data: [] }))
          ]);
          
          reports = studentReports.data;
          complaints = studentComplaints.data;
          break;

        default:
          break;
      }

      // Calculate statistics
      const stats = {
        totalReports: reports.length,
        pendingReports: reports.filter(report => 
          report.status === 'pending_prl' || report.status === 'pending_student'
        ).length,
        totalComplaints: complaints.length,
        pendingComplaints: complaints.filter(complaint => 
          complaint.status === 'pending'
        ).length,
        totalAssignments: assignments.length
      };

      setDashboardData({
        reports,
        complaints,
        assignments,
        stats
      });
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRoleSpecificTitle = () => {
    switch (user.role) {
      case 'pl': return 'Program Leader Dashboard';
      case 'prl': return 'Program Review Leader Dashboard';
      case 'fmg': return 'Faculty Management Dashboard';
      case 'lecturer': return 'Lecturer Dashboard';
      case 'student': return 'Student Dashboard';
      default: return 'Dashboard';
    }
  };

  const getRoleSpecificDescription = () => {
    switch (user.role) {
      case 'pl':
      case 'prl':
      case 'fmg':
        return 'Manage reports, complaints, and assignments across the faculty';
      case 'lecturer':
        return 'Manage your teaching reports and course assignments';
      case 'student':
        return 'View class reports and manage your complaints';
      default:
        return 'System Dashboard';
    }
  };

  if (loading) {
    return (
      <div className="dashboard">
        <div className="loading">Loading dashboard data...</div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="header-content">
          <h1>{getRoleSpecificTitle()}</h1>
          <p>{getRoleSpecificDescription()}</p>
        </div>
        <button onClick={onLogout} className="btn btn-secondary">
          Logout
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon" style={{ background: '#3498db' }}>
            <i>📊</i>
          </div>
          <div className="stat-content">
            <h3>{dashboardData.stats.totalReports}</h3>
            <p>Total Reports</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{ background: '#e74c3c' }}>
            <i>⏳</i>
          </div>
          <div className="stat-content">
            <h3>{dashboardData.stats.pendingReports}</h3>
            <p>Pending Reports</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{ background: '#f39c12' }}>
            <i>📝</i>
          </div>
          <div className="stat-content">
            <h3>{dashboardData.stats.totalComplaints}</h3>
            <p>Total Complaints</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{ background: '#9b59b6' }}>
            <i>⚠️</i>
          </div>
          <div className="stat-content">
            <h3>{dashboardData.stats.pendingComplaints}</h3>
            <p>Pending Complaints</p>
          </div>
        </div>

        {(user.role === 'pl' || user.role === 'lecturer') && (
          <div className="stat-card">
            <div className="stat-icon" style={{ background: '#27ae60' }}>
              <i>📚</i>
            </div>
            <div className="stat-content">
              <h3>{dashboardData.stats.totalAssignments}</h3>
              <p>Course Assignments</p>
            </div>
          </div>
        )}
      </div>

      {/* Navigation Tabs */}
      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        
        {(user.role === 'pl' || user.role === 'prl' || user.role === 'fmg') && (
          <>
            <button 
              className={`tab-btn ${activeTab === 'pending-reports' ? 'active' : ''}`}
              onClick={() => setActiveTab('pending-reports')}
            >
              Pending Reports
            </button>
            <button 
              className={`tab-btn ${activeTab === 'complaints' ? 'active' : ''}`}
              onClick={() => setActiveTab('complaints')}
            >
              Complaints
            </button>
            {user.role === 'pl' && (
              <button 
                className={`tab-btn ${activeTab === 'assignments' ? 'active' : ''}`}
                onClick={() => setActiveTab('assignments')}
              >
                Assignments
              </button>
            )}
          </>
        )}

        {user.role === 'lecturer' && (
          <>
            <button 
              className={`tab-btn ${activeTab === 'my-reports' ? 'active' : ''}`}
              onClick={() => setActiveTab('my-reports')}
            >
              My Reports
            </button>
            <button 
              className={`tab-btn ${activeTab === 'my-complaints' ? 'active' : ''}`}
              onClick={() => setActiveTab('my-complaints')}
            >
              My Complaints
            </button>
          </>
        )}

        {user.role === 'student' && (
          <>
            <button 
              className={`tab-btn ${activeTab === 'class-reports' ? 'active' : ''}`}
              onClick={() => setActiveTab('class-reports')}
            >
              Class Reports
            </button>
            <button 
              className={`tab-btn ${activeTab === 'my-complaints' ? 'active' : ''}`}
              onClick={() => setActiveTab('my-complaints')}
            >
              My Complaints
            </button>
          </>
        )}
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {activeTab === 'overview' && (
          <OverviewTab 
            user={user} 
            data={dashboardData} 
            onRefresh={loadDashboardData}
          />
        )}

        {activeTab === 'pending-reports' && (user.role === 'pl' || user.role === 'prl' || user.role === 'fmg') && (
          <PendingReportsTab 
            reports={dashboardData.reports} 
            user={user}
          />
        )}

        {activeTab === 'complaints' && (user.role === 'pl' || user.role === 'prl' || user.role === 'fmg') && (
          <ComplaintsTab 
            complaints={dashboardData.complaints}
            user={user}
          />
        )}

        {activeTab === 'assignments' && user.role === 'pl' && (
          <AssignmentsTab 
            assignments={dashboardData.assignments}
          />
        )}

        {activeTab === 'my-reports' && user.role === 'lecturer' && (
          <MyReportsTab 
            reports={dashboardData.reports}
          />
        )}

        {activeTab === 'class-reports' && user.role === 'student' && (
          <ClassReportsTab 
            reports={dashboardData.reports}
          />
        )}

        {activeTab === 'my-complaints' && (user.role === 'lecturer' || user.role === 'student') && (
          <MyComplaintsTab 
            complaints={dashboardData.complaints}
          />
        )}
      </div>
    </div>
  );
};

// Overview Tab Component
const OverviewTab = ({ user, data, onRefresh }) => {
  const getPendingActions = () => {
    const actions = [];
    
    if (data.stats.pendingReports > 0) {
      actions.push({
        type: 'report',
        count: data.stats.pendingReports,
        message: `${data.stats.pendingReports} reports pending your approval`,
        priority: 'high'
      });
    }
    
    if (data.stats.pendingComplaints > 0) {
      actions.push({
        type: 'complaint',
        count: data.stats.pendingComplaints,
        message: `${data.stats.pendingComplaints} complaints require your response`,
        priority: 'medium'
      });
    }

    return actions;
  };

  const pendingActions = getPendingActions();

  return (
    <div className="overview-tab">
      <div className="tab-header">
        <h2>Quick Overview</h2>
        <button onClick={onRefresh} className="btn btn-secondary">
          Refresh Data
        </button>
      </div>

      {pendingActions.length > 0 && (
        <div className="action-alerts">
          <h3>Action Required</h3>
          {pendingActions.map((action, index) => (
            <div key={index} className={`alert alert-${action.priority}`}>
              <span>{action.message}</span>
            </div>
          ))}
        </div>
      )}

      <div className="recent-activity">
        <h3>Recent Activity</h3>
        <div className="activity-list">
          {data.reports.slice(0, 5).map(report => (
            <div key={report.id} className="activity-item">
              <div className="activity-icon">📊</div>
              <div className="activity-content">
                <p><strong>{report.course_name}</strong> - Week {report.week_number}</p>
                <small>Status: {report.status} • {new Date(report.created_at).toLocaleDateString()}</small>
              </div>
            </div>
          ))}
          
          {data.reports.length === 0 && (
            <p className="no-data">No recent activity</p>
          )}
        </div>
      </div>
    </div>
  );
};

// Pending Reports Tab for Management
const PendingReportsTab = ({ reports, user }) => {
  const getStatusBadge = (status) => {
    const statusConfig = {
      pending_prl: { label: 'Pending PRL Approval', color: '#e67e22' },
      pending_student: { label: 'Pending Student Sign', color: '#f39c12' }
    };
    
    const config = statusConfig[status] || { label: status, color: '#95a5a6' };
    return (
      <span style={{
        background: config.color,
        color: 'white',
        padding: '0.25rem 0.5rem',
        borderRadius: '12px',
        fontSize: '0.8rem',
        fontWeight: 'bold'
      }}>
        {config.label}
      </span>
    );
  };

  return (
    <div className="pending-reports-tab">
      <h2>Reports Pending Approval</h2>
      <div className="data-table">
        <table>
          <thead>
            <tr>
              <th>Course</th>
              <th>Class</th>
              <th>Lecturer</th>
              <th>Week</th>
              <th>Date</th>
              <th>Students</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {reports.map(report => (
              <tr key={report.id}>
                <td>{report.course_name}</td>
                <td>{report.class_name}</td>
                <td>{report.lecturer_name}</td>
                <td>Week {report.week_number}</td>
                <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                <td>{report.students_present}</td>
                <td>{getStatusBadge(report.status)}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {reports.length === 0 && (
          <div className="no-data">
            <p>No reports pending approval</p>
          </div>
        )}
      </div>
    </div>
  );
};

