import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { reportAPI, complaintAPI, assignmentAPI } from '../services/api';

const Dashboard = ({ user }) => {
  const [stats, setStats] = useState({
    reports: 0,
    complaints: 0,
    assignments: 0,
    pending: 0
  });
  const [recentReports, setRecentReports] = useState([]);
  const [recentComplaints, setRecentComplaints] = useState([]);

  useEffect(() => {
    loadDashboardData();
  }, [user]);

  const loadDashboardData = async () => {
    try {
      // Load reports based on user role
      if (user.role === 'lecturer' || user.role === 'prl' || user.role === 'pl') {
        const reportsResponse = await reportAPI.getMyReports();
        setRecentReports(reportsResponse.data.slice(0, 5));
        setStats(prev => ({ ...prev, reports: reportsResponse.data.length }));
      } else if (user.role === 'student') {
        const reportsResponse = await reportAPI.getClassReports(user.class_id);
        setRecentReports(reportsResponse.data.slice(0, 5));
        setStats(prev => ({ ...prev, reports: reportsResponse.data.length }));
      }

      // Load complaints
      const complaintsResponse = await complaintAPI.getMyComplaints();
      setRecentComplaints(complaintsResponse.data.slice(0, 5));
      setStats(prev => ({ ...prev, complaints: complaintsResponse.data.length }));

      // Load assignments for lecturers
      if (user.role === 'lecturer' || user.role === 'prl' || user.role === 'pl') {
        const assignmentsResponse = await assignmentAPI.getMyAssignments();
        setStats(prev => ({ ...prev, assignments: assignmentsResponse.data.length }));
      }

    } catch (error) {
      console.error('Error loading dashboard data:', error);
    }
  };

  const getWelcomeMessage = () => {
    const roleNames = {
      student: 'Student Class Representative',
      lecturer: 'Lecturer',
      prl: 'Principal Lecturer',
      pl: 'Program Leader'
    };

    return `Welcome back, ${user.name}! You are logged in as ${roleNames[user.role]} in the ${user.faculty} faculty.`;
  };

  const getActions = () => {
    const baseActions = [
      {
        title: 'File a Complaint',
        description: 'Submit a complaint regarding academic matters',
        path: '/complaint',
        roles: ['student', 'lecturer', 'prl', 'pl']
      }
    ];

    if (user.role === 'student') {
      baseActions.unshift({
        title: 'View Class Reports',
        description: 'Check and sign lecture reports',
        path: '/report',
        roles: ['student']
      });
    }

    if (user.role === 'lecturer' || user.role === 'prl' || user.role === 'pl') {
      baseActions.unshift({
        title: 'Create Report',
        description: 'Submit a new lecture report',
        path: '/report',
        roles: ['lecturer', 'prl', 'pl']
      });
    }

    if (user.role === 'pl') {
      baseActions.push({
        title: 'Manage Assignments',
        description: 'Assign courses and classes to lecturers',
        path: '/assignments',
        roles: ['pl']
      });
    }

    return baseActions.filter(action => action.roles.includes(user.role));
  };

  return (
    <div className="dashboard">
      <div className="welcome-message">
        <h1>{getWelcomeMessage()}</h1>
        <p>Today is {new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Reports</h3>
          <div className="number">{stats.reports}</div>
        </div>
        <div className="stat-card">
          <h3>My Complaints</h3>
          <div className="number">{stats.complaints}</div>
        </div>
        {(user.role === 'lecturer' || user.role === 'prl' || user.role === 'pl') && (
          <div className="stat-card">
            <h3>My Assignments</h3>
            <div className="number">{stats.assignments}</div>
          </div>
        )}
        <div className="stat-card">
          <h3>Pending Actions</h3>
          <div className="number">{stats.pending}</div>
        </div>
      </div>

      <div className="actions-grid">
        {getActions().map((action, index) => (
          <Link key={index} to={action.path} className="action-card">
            <h3>{action.title}</h3>
            <p>{action.description}</p>
          </Link>
        ))}
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem', marginTop: '2rem' }}>
        <div>
          <h3 style={{ marginBottom: '1rem', color: '#fff' }}>Recent Reports</h3>
          <div className="data-table">
            {recentReports.length > 0 ? (
              recentReports.map(report => (
                <div key={report.id} style={{ padding: '1rem', borderBottom: '1px solid #444' }}>
                  <div style={{ fontWeight: 'bold' }}>{report.course_name}</div>
                  <div style={{ fontSize: '0.9rem', color: '#ccc' }}>
                    Week {report.week_number} • {report.status}
                  </div>
                </div>
              ))
            ) : (
              <div style={{ padding: '1rem', color: '#ccc' }}>No reports found</div>
            )}
          </div>
        </div>

        <div>
          <h3 style={{ marginBottom: '1rem', color: '#fff' }}>Recent Complaints</h3>
          <div className="data-table">
            {recentComplaints.length > 0 ? (
              recentComplaints.map(complaint => (
                <div key={complaint.id} style={{ padding: '1rem', borderBottom: '1px solid #444' }}>
                  <div style={{ fontWeight: 'bold' }}>{complaint.title}</div>
                  <div style={{ fontSize: '0.9rem', color: '#ccc' }}>
                    Status: {complaint.status}
                  </div>
                </div>
              ))
            ) : (
              <div style={{ padding: '1rem', color: '#ccc' }}>No complaints found</div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
